//LOGIN DONE BY BOTH THE CUSTOMER AND ADMIN    
    private void loginbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_loginbtnActionPerformed
       String email = EmaillogTxt1.getText().trim();
      String password = PasswordlogTxt1.getText().trim();
    if (email.isEmpty() || password.isEmpty()) {
        JOptionPane.showMessageDialog(this, "Please enter both email and password", "VALIDATION MESSAGE", JOptionPane.WARNING_MESSAGE);
        return;
    }
    CustomerDAO dao = new CustomerDAO();
    Customer customer = dao.authenticateUser(email, password);
    if (customer != null) {
        String roleName = dao.getRoleName(customer.getRole_Id());
        JOptionPane.showMessageDialog(this, "Welcome, " + customer.getName() + "! You have logged in as " + roleName + ".", "INFORMATION MESSAGE", JOptionPane.INFORMATION_MESSAGE);
        if ("Admin".equals(roleName)) {
            setVisible(false);
            AdminPage adminForm = new AdminPage();
            adminForm.setVisible(true);
        } else if ("Customer".equals(roleName)) {
            setVisible(false);
            CustomerPage customerForm = new CustomerPage(customer.getCustomerId());
            customerForm.setVisible(true);
        }
    } else {
        JOptionPane.showMessageDialog(this, "Invalid email or password", "ERROR MESSAGE", JOptionPane.ERROR_MESSAGE);
    }
    }//GEN-LAST:event_loginbtnActionPerformed

